import { useState } from 'react';

const Button=(b)=>(
  <button onClick={b.handleClick}>
    {b.text}
  </button>
)
const Stats=(statsB)=>{
  return(
    <tr>
      <p>{statsB.name}</p>
      <p>{statsB.value}</p>
    </tr>
  )
}
const StatsGiven=(statsB)=>{
  if(!statsB.isClicked){
    return <p>No feedback has been given yet</p>
  }
}

const App = () => {
  // save buttons to their own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  let total=good+neutral+bad

  const average=getWAverage(
    [
      {
        number: good, weight: 1
      }
    ]
  )

  //when clicked, check which button and change value accordingly
  const handleClick=(which)=>{
    setIsClicked(true)

    if (which="good"){
      setGood(good+1)
    }if (which="neutral") {
      setNeutral(neutral+1)
    } else {
      setBad(bad+1)
    }
  }
  const statsB={
    good:good,
    neutral:neutral,
    bad:bad,
    total:total,
    average:average,
    percPos:percPos,
    isClicked:isClicked
  }

  return (
    <div>
      <h1>give feedback</h1>
      <Button onClick={()=> handleClick("good")} text="good"/>
      <Button onClick={()=> handleClick("neutral")} text="neutral"/>
      <Button onClick={()=> handleClick("bad")} text="bad"/>
      <StatsGiven {...statsB} />
    </div>
  )
}

export default App
