  <button onClick={b.onClick}>
    {b.text}
  </button>
)

const Stats=(statsB)=>{
  return(
    <tr>
      <p>{statsB.name}</p>
      <p>{statsB.value}</p>
    </tr>
  )
}

const Statistics=(statsB)=>{
  if(!statsB.isClicked){
    return <p>No feedback has been given yet</p>
  }else{
    return(
      <div>
      <h3>Stats on votes</h3>
